import { Ionicons } from "@expo/vector-icons";
import { createStackNavigator } from "@react-navigation/stack";
import * as React from "react";
import { TouchableOpacity } from "react-native";

import Bateau from "../screen/Bateau";
import Contact from "../screen/Contact";
import Detail from "../screen/Detail";
import Accueil from "../screen/Accueil";
import Panier from "../screen/PanierScreen";
import ProduitDetail from "../screen/ProduitDetail";
import Produit from "../screen/Produit";
import Recette from "../screen/Recette";
import Restaurant from "../screen/Restaurant";
const Stack = createStackNavigator();

const ICON_SIZE = 24;
const MARGIN = 20;

const HomeButton = ({ navigation }) => (
    <TouchableOpacity
        onPress={() => navigation.navigate("Accueil")}
        style={{ marginLeft: MARGIN }}
    >
        <Ionicons name="md-home" size={ICON_SIZE} color="white" />
    </TouchableOpacity>
);

const CartButton = ({ navigation }) => (
    <TouchableOpacity
        onPress={() => navigation.navigate("Panier")}
        style={{ marginRight: MARGIN }}
    >
        <Ionicons name="md-cart" size={ICON_SIZE} color="white" />
    </TouchableOpacity>
);

function screenOptionStyle({ route, navigation }) {
    const isAccueil = route.name === "Accueil";

    return {
        headerMode: "screen",
        headerTintColor: "black",
        headerBackTitle: "Back",
        gestureEnabled: true,
        headerStyle: { backgroundColor: "black" },
        headerLeft: !isAccueil
            ? () => <HomeButton navigation={navigation} />
            : null,
        headerRight: !isAccueil
            ? () => <CartButton navigation={navigation} />
            : null,
        headerShown: isAccueil ? false : true,
    };
}

const MainStackNavigator = () => (
    <Stack.Navigator
        initialRouteName="Accueil"
        screenOptions={screenOptionStyle}
    >
        <Stack.Screen name="Accueil" component={HomeScreen} />
        <Stack.Screen name="Produits" component={ProduitScreen} />
        <Stack.Screen name="Bateaux" component={BateauScreen} />
        <Stack.Screen name="Restaurants" component={RestaurantScreen} />
        <Stack.Screen name="Recettes" component={RecetteScreen} />
        <Stack.Screen name="Contact" component={ContactScreen} />
        <Stack.Screen name="Nos bateaux partenaires" component={DetailScreen} />
        <Stack.Screen
            name="Nos restaurants partenaires"
            component={DetailScreen}
        />
        <Stack.Screen name="Nos recettes" component={DetailScreen} />
        <Stack.Screen
            name="Choisissez vos produits"
            component={ProduitDetailScreen}
        />
        <Stack.Screen name="Panier" component={PanierScreen} />
    </Stack.Navigator>
);

const PanierStackNavigator = () => {
    return (
        <Stack.Navigator screenOptions={screenOptionStyle}>
            <Stack.Screen name="Panier" component={PanierScreen} />
        </Stack.Navigator>
    );
};

export { MainStackNavigator, PanierStackNavigator };
