import * as React from "react";
import { createStackNavigator } from "@react-navigation/stack";
import { Image, TouchableOpacity } from 'react-native';
import { Ionicons } from "@expo/vector-icons"; 

import HomeScreen from "../screen/HomeScreen";
import ContactScreen from "../screen/ContactScreen";
import BateauScreen from "../screen/BateauScreen";
import RestaurantScreen from "../screen/RestaurantScreen";
import RecetteScreen from "../screen/RecetteScreen";
import ProduitScreen from "../screen/ProduitScreen";
import PanierScreen from "../screen/PanierScreen";
import DetailScreen from "../screen/DetailScreen";
import ProduitDetailScreen from "../screen/ProduitDetailScreen";
import ShoppingCart from "../component/ShoppingCart";
import HomeButton from "../component/HomeButton";

const Stack = createStackNavigator();

function screenOptionStyle({ route, navigation }) {
  const headerOptions = {
    headerMode: "screen",
    headerTintColor: "black",
    headerBackTitle: "Back",
    gestureEnabled: true,
    headerStyle: { backgroundColor: 'black' },
    headerBackground: HeaderTitle,
  };

  const iconSize = 24;

  if (route.name !== "Accueil") {
    headerOptions.headerLeft = () => (
      <TouchableOpacity
        onPress={() => {
          navigation.navigate("Accueil");
        }}
        style={{ marginLeft: 20 }}
      >
        <Ionicons name="md-home" size={iconSize} color="white" />
      </TouchableOpacity>
    );

    headerOptions.headerRight = () => (
      <TouchableOpacity
        onPress={() => {
          navigation.navigate("Panier");
        }}
        style={{ marginRight: 20 }}
      >
        <Ionicons name="md-cart" size={iconSize} color="white" />
      </TouchableOpacity>
    );
  } else {
    headerOptions.headerShown = false;
  }

  return headerOptions;
}

const HeaderTitle = () => (
  <Image
    backgroundColor='black'
    source={require('../assets/img/navbarbg.png')}
    style={{
      width: '100%',
      height: 100, // Ajustez la hauteur de l'image selon vos besoins
      flex: 1,
      resizeMode: 'contain'
    }}
  />
);

const MainStackNavigator = () => {
  return (
    <Stack.Navigator
      initialRouteName="Accueil"
      screenOptions={screenOptionStyle}
    >
      {/* ... Vos Ã©crans existants */}
    </Stack.Navigator>
  );
};

const PanierStackNavigator = () => {
  return (
    <Stack.Navigator
      screenOptions={screenOptionStyle}
    >
      <Stack.Screen name="Panier" component={PanierScreen} />
    </Stack.Navigator>
  );
};

export { MainStackNavigator, PanierStackNavigator };
