import React from "react";
import {
  Text,
  StyleSheet,
  SafeAreaView,
  ImageBackground,
  View,
  TouchableOpacity,
  FlatList,
  Image,
} from "react-native";
import CoordonneesComponent from "../component/CoordonneesComponent";
import globalStyles from "../component/GlobalStyle";

const boats = [
  {
    name: "De la Brise",
    image: require("../assets/img/deLaBrise.png"),
    icon: require("../assets/img/deLaBrise_icon.png"),
    description: "XXX YYY ZZZ",
    about: `Qu'il est chaud le soleil\nQuand nous sommes en vacances...\n`,
  },
  {
    name: "Saphir",
    image: require("../assets/img/saphir.png"),
    icon: require("../assets/img/saphir_icon.png"),
    description: "XXX YYY ZZZ",
    about: `Qu'il est chaud le soleil\nQuand nous sommes en vacances...\n`,
  },
  {
    name: "Gast Micher",
    image: require("../assets/img/gastMicher.png"),
    icon: require("../assets/img/gastMicher_icon.png"),
    description: "XXX YYY ZZZ",
    about: `Qu'il est chaud le soleil\nQuand nous sommes en vacances...\n`,
  },
  {
    name: "Aquilon",
    image: require("../assets/img/aquilon.png"),
    icon: require("../assets/img/aquilon_icon.png"),
    description: "XXX YYY ZZZ",
    about: `Qu'il est chaud le soleil\nQuand nous sommes en vacances...\n`,
  },
];

const BateauScreen = (props) => {
  const BeforeBateauScreen = () => {
    return (
      <View style={styles.descriptionArea}>
        <Text style={styles.titre}>Nos bateaux partenaires</Text>
        <Text style={styles.bold}>
          Tous les eaux mènent à Thibault.
        </Text>
        <CoordonneesComponent />
      </View>
    );
  };

  return (
    <SafeAreaView style={globalStyles.container}>
      <ImageBackground
        source={require("../assets/img/background.png")}
        resizeMode="cover"
        style={globalStyles.background}
      >
        <View>
          <View style={styles.boutonsArea}>
            <FlatList
              data={boats}
              ListHeaderComponent={BeforeBateauScreen}
              keyExtractor={(item, index) => item.name}
              numColumns={2}
              renderItem={({ item }) => (
                <TouchableOpacity
                  style={styles.boutonAvecIcon}
                  onPress={() =>
                    item.description
                      ? props.navigation.navigate("Nos bateaux partenaires", {
                          name: item.name,
                          image: item.image,
                          descriptioncom: item.description,
                          aboutus: item.about,
                        })
                      : props.navigation.navigate("Contact")
                  }
                >
                  {item.description ? (
                    <>
                      <Image style={styles.icon} source={item.icon} />
                      <Text style={styles.boutonTexte}>{item.name}</Text>
                    </>
                  ) : (
                    <>
                      <Image
                        style={styles.icon}
                        source={require("../assets/img/ancre.png")}
                      />
                      <Text style={styles.boutonTexte}>Contact</Text>
                    </>
                  )}
                </TouchableOpacity>
              )}
            />
          </View>
        </View>
      </ImageBackground>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  descriptionArea: {
    // Styles for the description area
  },
  titre: {
    // Styles for the title
  },
  bold: {
    // Styles for bold text
  },
  boutonsArea: {
    // Styles for the button area
  },
  boutonAvecIcon: {
    // Styles for the button with icon
  },
  icon: {
    // Styles for the icon
  },
  boutonTexte: {
    // Styles for the button text
  },
});

export default BateauScreen;
