const React = require("react");
const {
  View,
  Text,
  StyleSheet,
  TouchableOpacity,
  ImageBackground,
  ScrollView,
} = require("react-native");

const {
  MaterialCommunityIcons,
  MaterialIcons,
} = require("@expo/vector-icons");
const globalStyles = require("../component/GlobalStyle");
const CoordonneesComponent = require("../component/CoordonneesComponent");

function HomeScreen(props) {
  return (
    React.createElement(View, { style: globalStyles.container },
      React.createElement(ImageBackground, {
        source: require("../assets/img/background.png"),
        resizeMode: "cover",
        style: globalStyles.background
      },
        React.createElement(ScrollView, null,
          React.createElement(View, { style: globalStyles.descriptionArea },
            React.createElement(Text, { style: styles.titre }, "Le Bateau de Thibault"),
            React.createElement(Text, null, "Vente en direct de notre bateau"),
            React.createElement(Text, null, "Produits selon la saison, livraisons sur Paris"),
            React.createElement(CoordonneesComponent, null)
          ),
          React.createElement(View, { style: globalStyles.boutonsArea },
            React.createElement(TouchableOpacity, {
              style: styles.boutonSoloStyle,
              onPress: () => props.navigation.navigate("Produits")
            },
              React.createElement(Text, { style: globalStyles.boutonTexte },
                React.createElement(MaterialCommunityIcons, { name: "fish", style: styles.iconStyle }),
                "Produits et Promotions"
              )
            ),
            React.createElement(View, { style: globalStyles.boutonDuoStyle },
              React.createElement(TouchableOpacity, {
                style: globalStyles.boutonGeneriqueStyle,
                onPress: () => props.navigation.navigate("Bateaux")
              },
                React.createElement(Text, { style: globalStyles.boutonTexte },
                  React.createElement(MaterialCommunityIcons, { name: "anchor", style: styles.iconStyle }),
                  "Bateaux"
                )
              ),
              React.createElement(TouchableOpacity, {
                style: globalStyles.boutonGeneriqueStyle,
                onPress: () => props.navigation.navigate("Restaurants")
              },
                React.createElement(Text, { style: globalStyles.boutonTexte },
                  React.createElement(MaterialIcons, { name: "storefront", style: styles.iconStyle }),
                  "Restaurants"
                )
              )
            ),
            React.createElement(View, { style: globalStyles.boutonDuoStyle },
              React.createElement(TouchableOpacity, {
                style: globalStyles.boutonGeneriqueStyle,
                onPress: () => props.navigation.navigate("Recettes")
              },
                React.createElement(Text, { style: globalStyles.boutonTexte },
                  React.createElement(MaterialCommunityIcons, { name: "silverware-clean", style: styles.iconStyle }),
                  "Recettes"
                )
              ),
              React.createElement(TouchableOpacity, {
                style: globalStyles.boutonGeneriqueStyle,
                onPress: () => props.navigation.navigate("Contact")
              },
                React.createElement(Text, { style: globalStyles.boutonTexte },
                  React.createElement(MaterialCommunityIcons, { name: "jellyfish-outline", style: styles.iconStyle }),
                  "Contact"
                )
              )
            )
          )
        )
      )
    )
  );
}

const styles = StyleSheet.create({
  titre: {
    fontStyle: 'italic', // Applique le style italique à la police
    fontSize: 24,
    fontWeight: 'bold', // Par exemple, utilisez fontWeight pour le gras si nécessaire
    color: 'white', // Couleur du texte en blanc
  },

  boutonSoloStyle: {
    marginHorizontal: "1%",
    marginBottom: 10,
    backgroundColor: "rgba(0, 0, 0, 0.1)",
    padding: 20,
    width: "98%",
  },

  iconStyle: {
    fontSize: 30,
    color: "black",
  },
});

module.exports = HomeScreen;
