import { createSlice } from '@reduxjs/toolkit'

export const basketSlice = createSlice({
  name: 'basket',
  initialState: {
    items: {}, // Utilisation d'un nom plus explicite
    nbItems: 0,
  },
  reducers: {
    addToBasket: (state, action) => {
      const { product } = action.payload;
      if (!state.items[product]) {
        state.items[product] = 1;
      } else {
        state.items[product]++;
      }
      state.nbItems++;
    },

    removeFromBasket: (state, action) => {
      const { product } = action.payload;
      if (state.items[product] > 0) {
        state.items[product]--;
        state.nbItems--;
      }
    },

    removeAllFromBasket: (state) => {
      state.items = {};
      state.nbItems = 0;
    },

    setQuantity: (state, action) => {
      const { product, quantity } = action.payload;
      state.nbItems -= state.items[product] || 0;
      state.items[product] = quantity;
      state.nbItems += quantity;
    },
  },
})

// Action creators are generated for each case reducer function
export const { addToBasket, removeFromBasket, setQuantity, removeAllFromBasket } = basketSlice.actions

export default basketSlice.reducer;
